package day.firstprogram;

public class Example1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Num1 = 10;
		int Num2 = 30;
		int Result;
		Result = Num1 + Num2;
		System.out.println("Result is :" + Result);
		Result = Num1 - Num2;
		System.out.println("Result is :" + Result);
		Result = Num1 * Num2;
		System.out.println("Result is :" + Result);
		Result = Num1 / Num2;
		System.out.println("Result is :" + Result);

	}

}
